import React, { useState } from 'react';
import { NavLink as Link } from 'react-router-dom';
const Sidebar = ()=>{
    const [isLogedIn, setIsLogedIn] = useState(false);
    const [activeUser, setActiveUser] = useState('null');
    const [jobtitle, setJobtitle] = useState('')
    setActiveUser(JSON.parse(sessionStorage.getItem('LoggedIn')));
    Sidebar.handleLogOut = function() {
        setActiveUser(null);
        sessionStorage.removeItem('LoggedIn');
        sessionStorage.removeItem('expiresAt');
        setIsLogedIn(false);
    }
    console.log(activeUser);
    // function  to swith off some link element instead of onclick/ like 3 for leve and one for logedin
    setJobtitle((activeUser === null)? 'null': activeUser.jobtitle);
    Sidebar.activeUser = activeUser;
    return (

        <div className="sidebar">
            <ul>
                <li><Link to="/">Home</Link></li>
                {!isLogedIn && (
                    <li><Link to="/Login" >Login</Link></li>
                )}
                {(jobtitle==='Manager'||jobtitle==='Tracker') && (
                    <li><Link to="/Materials">Material</Link></li>
                )}
                {(jobtitle==='Manager'||jobtitle==='Tracker') && (
                    <li><Link to="/NewRecord">New Records</Link></li>
                )}
                {(jobtitle==='Manager') && (
                    <li><Link to="/Administrator" >Administrator</Link></li>
                )}
                {(jobtitle==='Manager'||jobtitle==='Tracker') && (
                    <li><Link to="/Attendence" >Attendence</Link></li>
                )}
                {(jobtitle==='Manager') && (
                    <li><Link to="/Employees" >Employees</Link></li>
                )}
                {!isLogedIn && (
                    <li><Link to="/History" >History</Link></li>
                )}
                <li><Link to="/Register" >Register</Link></li>
                <li><Link to="/Logout" >Logout</Link></li>
            </ul>
        </div>
    )
}

export default Sidebar
