import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Attendence = ( { data } ) => {
    const [users, setUsers] = useState([]);
    const [loading, setLoading] = useState(true);
    const [editedField, setEditedField] = useState({});
    const [activeUser, setActiveUser] = useState('');
    const [submitting, setSubmitting] = useState(false);
    const [error, setError] = useState(null);
    const status = data.jobtitle;
    setActiveUser(JSON.parse(sessionStorage.getItem('LoggedIn')));
    useEffect(() => {
        fetch(`http://localhost:8000/api/attendance/${status}`,{
            method:'PUT',
            headers: {'Content-Type':"application/json"},
            body:JSON.stringify({}),
        })
        .then(response => response.json())
        .then(data => {
            setUsers(data);
            setLoading(false);
        })
        .catch (error => {
            setError(error);
            setLoading(false);    
        });
    }, [status]);

    if (loading) {
        return <p>Loading...</p>
    }

    if (error){
        return <p>Error: {error.message}</p>
    }

    const handleEdit= (index,field,value) =>{
        setEditedField(prevState => ({...prevState, [index]:{...prevState[index], [field]:value } }));
    };

    const handleSubmit = (tellNo,index) => {
        setSubmitting(true);
        axios.post(`http://localhost:8000/api/attendance/${tellNo}`,{
            method:'POST',
            headers: {'Content-Type':"application/json"},
            body:JSON.stringify(editedField[index]),
        })
        .then(response => response.json())
        .then(data => {
            setUsers(data);
            setEditedField({});
            setSubmitting(false);
        });
    };
    const handleSubmitAll = () => {
        axios.post('http://localhost:8000/api/attendance/all',{
            method:'POST',
            headers: {'Content-Type':"application/json"},
            body:JSON.stringify(editedField),
        })
        .then(response => response.json())
        .then(data => setUsers(data));
        handleRefresh();
    }

    const handleRefresh = async () => {
        try {
            const response = await fetch('http://localhost:8000/api/attendance/',{
                method:'POST',
                headers: {'Content-Type':"application/json"},
                body:JSON.stringify({jobtitle:status}),
            });
            const data = await response.json();
            setUsers(data);
        }catch (error){
            return <p>There is no data</p>
        }
    }

    return (
        <div>
            <h1>
                Employee List 
                <button onClock={handleRefresh} style= {{marginLeft:10}}>
                    Refresh
                </button>
            </h1>
            <table>
                <thead>
                    <tr>
                        <th>Date</th>
                        <th>Day Id</th>
                        <th>Username</th>
                        <th>PhoneNumber</th>
                        <th>Arrival</th>
                        <th>Depature</th>
                        <th>Submit</th>
                    </tr>
                </thead>
                <tbody>
                    {users.map((user, index) =>(
                        <tr key={user.tellNo}>
                            <td>{user.date}</td>
                            <td>{user.dayid}</td>
                            <td>{user.username}</td>
                            <td>{user.tellNo}</td>
                            <td>
                                {editedField[index].tellNo=user.tellNo}
                                {editedField[index].date=user.date}
                                {editedField[index].dayid=user.dayid}
                                {editedField[index].recorder = activeUser.username}
                                {editedField[index] && editedField[index].arrival? (
                                <input type="time" value={editedField[index].arrival} onChange={(e) => handleEdit(index, 'arrival',e.target.value)} />
                                ):(
                                    <span>{user.arival}</span>
                                )}
                            </td>
                            <td>
                                {editedField[index] && editedField[index].depature? (
                                <input type="time" value={editedField[index].depature} onChange={(e) => handleEdit(index, 'depature',e.target.value)} />
                                ):(
                                    <span>{user.depature}</span>
                                )}
                            </td>
                            <td>
                                <button onClick={() => handleSubmit(user.tellNo,index)} disabled={submitting=== index} > Submit</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            <button onClick={handleSubmitAll}>Submit All</button>
        </div>
    );
}

export default Attendence;
