import React from 'react';
import {BrowserRouter as Router, Routes as Switch, Route } from 'react-router-dom';
import Login from './pages/Login';
import Register from './pages/Register';
import Materials from './pages/Materials';
import NewRecords from './pages/NewRecords';
import Employees from './pages/Employees';
import History from './pages/History';
import './App.css';
import Sidebar from './components/Navbar';
import Home from './components/Home';
import Attendence from './pages/Attendance';
import Logout from './pages/Logout';
import Adminitrator from './pages/Administrator';

function App() {

  return (
    <Router>
      <Sidebar />
      <Switch >
        <Route path="/" exact component={Home} />
        <Route />
        <Route path="/Logout" component={() => <Logout data={ Sidebar.activeUser} onFetch={Sidebar.handleLogOut()} />} /> 
        <Route path="/Login" component={Login} />
        <Route path="/Register" component={Register} />
        <Route path="/Materials" component={()=><Materials data={Sidebar.activeUser}/>} />
        <Route path="/NewRecord" component={()=><NewRecords data={Sidebar.activeUser} />} />
        <Route path="/Administrator" component={()=> <Adminitrator data={Sidebar.activeUser} />} />
        <Route path="/Attendences" components={()=> <Attendence data={Sidebar.activeUser} />} />
        <Route path="/Employees" component={() => <Employees data={Sidebar.activeUser} />} />
        <Route path="/History" component={() => <History data={Sidebar.activeUser} />} />
      </Switch> 
    </Router>
  );
}

export default App
