import React, { useState} from 'react'

function History({data}){
    const [userData, setUserData] = useState('');
    const [userInformation, setUserInformation ] = useState('');
    const [numb, setNumb ] = useState('');
    const [activeUser, setActiveUser ] = useState('');
    
    setActiveUser(JSON.parse(sessionStorage.getItem('LoggedIn')))
    const getUserInformation = async ()=>{
        fetch('http://localhost:8000/api/Myinfo/',{
            method:'POST',
            headers: {'Content-Type':"application/json"},
            body:JSON.stringify({'tellNo':activeUser.tellNo}),
        })
        .then(response => response.json())
        .then(smallInfor => {
            setUserInformation(smallInfor);
        })
        .catch (error => {
            console.log(error);
        });
    }
    function handleChange(str){
        setNumb(str);
        getuserData();
    }
    setNumb((data.tellNo===null)? data.tellNo:activeUser.tellNo)
    const getuserData = async ()=> {
        fetch('http://localhost:8000/api/Mydata/',{
            method:'GET',
            headers: {'Content-Type':"application/json"},
            body:JSON.stringify({'tellNo':numb}),
        })
        .then(response => response.json())
        .then(Bigdata => {
            setUserData(Bigdata);
        })
        .catch (error => {
            console.log(error);
        });
    }
    getUserInformation();
    getuserData();

    return (
        <>
        <div id="section1">
            {(activeUser.username===userInformation.username ||data.jobtitle==='Manager')? (
                <ul>
                    <span>{userInformation.firstname} {userInformation.sirname}</span>
                    <li>tellNo: {userInformation.tellNo}</li>
                    <li>Job: {userInformation.jobtitle}</li>
                    <li>Email: {userInformation.email}</li>
                    <li>Physical Address: {userInformation.address}</li>
                </ul>
            ):(
                <p>........UNAUTHORISED.......</p>
            )}
        </div>
        <div id="section2">
            {(activeUser.jobtitle==='Manager')&&
            <input value={numb} placeHolder="Enter phone number to lookup" onChange={(e)=>{handleChange(e.target.value)}}/>
            }
        <table>
                <thead>
                    <tr>
                        <th>Date</th>
                        <th>Arival</th>
                        <th>Depature</th>
                        <th>Tracker</th>
                    </tr>
                </thead>
                <tbody>
                    {userData.map(dates =>(
                        <tr key={dates.date}>
                            <td>{dates.date}</td>
                            <td>{dates.rrival}</td>
                            <td>{dates.depature}</td>
                            <td>{dates.recorder}</td>
                        </tr>
                    ))}
                </tbody>
        </table>
        </div>
        </>
    )
}

export default History
