import React, { useState } from 'react'
import axios from 'axios'

const Login = ({onSuccess}) => {
    const [email, setEmail] = useState('');
    const [tellNo, settellNo] = useState('')
    const [password, setPassword] = useState('');
    const [choice, setChoice] = useState('email')
    const handleSubmit = async (event) => {
        event.preventDefault();
        try{
            const response =await axios.post('http://localhost:8000/api/login/', {
                email,
                tellNo,
                password,
                choice,
            });
            console.log(response.data,response.status);
            if (response.status ==='Logged In'){
                onSuccess();
                const LoggdIn = response.data;
                sessionStorage.setItem('LoggedIn',JSON.stringify(LoggdIn));
                sessionStorage.setItem('expiresAt',Date.now()+3600000);
                
            }

        }catch (error){
            if (error.response && error.response.data){
                console.error(error.response.data);//iff response.data is defined
            }else{
                console.error(error.message);
            }
        }
    };
    function LoginMethod(){
        if (choice==='tellNo'){
            return (
                <>
                    <label>
                        <input type="radio" name="choice" value="tellNo" checked={choice === 'tellNo'} onChange={(e) =>setChoice(e.target.value)} />
                        Login with Tellphone Number
                    </label>
                    <br/>
                    <label>PhoneNumber:</label>
                    <input type="text" value={tellNo} onChange={(event) => settellNo(event.target.value)} />
                    <br />
                </>
            )
        }
        return (
            <>
                <label>                    
                <input type="radio" name="choice" value="email" checked={choice === 'email'} onChange={(e) =>setChoice(e.target.value)} />
                Login with Email
                </label>
                <label>Email:</label>
                <input type="email" value={email} onChange={(event) => setEmail(event.target.value)} />
                <br />
            </>
        )
    }
    return (
        <div>
            <h3>Login</h3>
            <form onSubmit={handleSubmit}>
                {LoginMethod()}
                <label>Password</label>
                <input type="password" value={password} onChange={(event) => setPassword(event.target.value)} />
                <br />
                <button type="submit">Login</button>
            </form>
        </div>
    );
};

export default Login;
