import React, { useState, useEffect } from 'react';
import { NavLink as Link } from 'react-router-dom';

const Sidebar = () => {
    const [isLogedIn, setIsLogedIn] = useState(false);
    const [activeUser, setActiveUser] = useState(null);
    const [jobtitle, setJobtitle] = useState('null');

    // Load session storage data only once when the component mounts
    useEffect(() => {
        const loggedInUser = JSON.parse(sessionStorage.getItem('LoggedIn'));
        setActiveUser(loggedInUser);
        setJobtitle(loggedInUser ? loggedInUser.jobtitle : 'null');
        setIsLogedIn(!!loggedInUser);
    }, []);

    Sidebar.handleLogOut = () => {
        setActiveUser(null);
        sessionStorage.removeItem('LoggedIn');
        sessionStorage.removeItem('expiresAt');
        setIsLogedIn(false);
    };

    Sidebar.activeUser = activeUser;

    return (
        <div className="sidebar">
            <ul>
                <li><Link to="/">Home</Link></li>
                {!isLogedIn && <li><Link to="/Login">Login</Link></li>}
                {(jobtitle === 'Manager' || jobtitle === 'Tracker') && <li><Link to="/Materials">Material</Link></li>}
                {(jobtitle === 'Manager' || jobtitle === 'Tracker') && <li><Link to="/NewRecord">New Records</Link></li>}
                {jobtitle === 'Manager' && <li><Link to="/Administrator">Administrator</Link></li>}
                {(jobtitle === 'Manager' || jobtitle === 'Tracker') && <li><Link to="/Attendence">Attendence</Link></li>}
                {jobtitle === 'Manager' && <li><Link to="/Employees">Employees</Link></li>}
                {isLogedIn && <li><Link to="/History">History</Link></li>}
                <li><Link to="/Register">Register</Link></li>
                {isLogedIn && <li><Link to="/Logout">Logout</Link></li>}
            </ul>
        </div>
    );
};

export default Sidebar;
